mapper.select:
select *
from myblog.t_user
where username = #{username}
  and role = 'user'
limit #{startIndex},#{pageSize};

select *
from myblog.t_user
where nickname = #{nickname}
  and role = 'user'
limit #{startIndex},#{pageSize};

select *
from myblog.t_user
where username = #{username}
  and nickname = #{nickname}
  and role = 'user'
limit #{startIndex},#{pageSize};

select *
from myblog.t_user
where username = #{username}
    and role = 'admin'
   or role = 'root'
limit #{startIndex},#{pageSize};

select *
from myblog.t_user
where nickname = #{nickname}
    and role = 'admin'
   or role = 'root'
limit #{startIndex},#{pageSize};

select *
from myblog.t_user
where username = #{username}
    and nickname = #{nickname}
    and role = 'admin'
   or role = 'root'
limit #{startIndex},#{pageSize};

mapper.update:
update myblog.t_user
set avatar=#{avatar}
where id = #{id}
